const loginButton=document.querySelector("#loginButton");function showLoginForm(){document.querySelector(".form-box").style.display="block"}function hideLoginForm(){document.querySelector(".form-box").style.display="none"}loginButton.addEventListener("click",showLoginForm);const formBoxCloseBtn=document.querySelector(".form-box__close__btn");function validateForm(){const o=document.getElementById("login"),t=document.getElementById("password"),e=o.value,n=t.value;let l=!1;if(passObj.forEach(function(o){o.login!==e||o.password!==n||(l=!0)}),l)return localStorage.setItem("login",e),localStorage.setItem("password",n),localStorage.setItem("autoLogIn",!0),!0;return document.querySelector(".modal").style.position="fixed",document.querySelector(".incorrect").style.display="block",!1}formBoxCloseBtn.addEventListener("click",hideLoginForm);const visitButton=document.querySelector(".visitButton"),submitButton=document.getElementById("submit-button");submitButton.addEventListener("click",function(o){o.preventDefault(),validateForm()&&(hideLoginForm(),loginButton.style.display="none",visitButton.style.display="block")});export const passObj=[{login:"admin@gmail.com",password:"qwerty123"},{login:"admin",password:"qwerty123"}];