const loginButton=document.querySelector("#loginButton");function showLoginForm(){document.querySelector(".form-box").style.display="block"}function hideLoginForm(){document.querySelector(".form-box").style.display="none"}loginButton.addEventListener("click",showLoginForm);const formBoxCloseBtn=document.querySelector(".form-box__close__btn");function validateForm(){const t=document.getElementById("login"),o=document.getElementById("password"),e=t.value,n=o.value;return passObj.forEach(t=>{if(t.login===login.value&&t.password===password.value)return localStorage.setItem("login",login.value),localStorage.setItem("password",password.value),localStorage.setItem("autoLogIn",!0),this.getFormCloseAction(),this.dataValue=!0,this.dataValue}),""!==e.trim()&&""!==n.trim()||(alert("Пожалуйста, заполните все поля формы."),!1)}formBoxCloseBtn.addEventListener("click",hideLoginForm);const visitButton=document.querySelector(".visitButton"),submitButton=document.getElementById("submit-button");submitButton.addEventListener("click",function(t){t.preventDefault(),validateForm()&&(hideLoginForm(),loginButton.style.display="none",visitButton.style.display="block")});export let passObj=[{login:"admin@gmail.com",password:"qwerty123"}];